import{_ as l,C as e,c as n,o,am as r,G as i}from"./chunks/framework.SAy2jMg1.js";const g=JSON.parse('{"title":"物品条件","description":"","frontmatter":{"breadcrumbs":[{"title":"Continue Project","link":"/AuraSkills"},{"title":"物品条件","link":"/AuraSkills/skills.item-requirements"}]},"headers":[],"relativePath":"AuraSkills/skills.item-requirements.md","filePath":"AuraSkills/skills.item-requirements.md"}'),p={name:"AuraSkills/skills.item-requirements.md"};function d(c,s,h,k,m,u){const a=e("NolebaseGitContributors"),t=e("NolebaseGitChangelog");return o(),n("div",null,[s[0]||(s[0]=r(`<h1 id="物品条件" tabindex="-1">物品条件 <a class="header-anchor" href="#物品条件" aria-label="Permalink to &quot;物品条件&quot;">​</a></h1><p>物品和盔甲条件的教程</p><p><strong>条件（Requirements）</strong> 允许你为物品添加技能等级限制。例如，你可以创建一把只允许挖掘等级 15 以上玩家使用的镐子。限制方法有两种：物品与盔甲。</p><p>物品条件在玩家持有时检查。装备条件只在玩家穿戴时检查。</p><p>这里有两种不同的方法添加条件：通过命令或全局条件。</p><h2 id="命令条件" tabindex="-1">命令条件 <a class="header-anchor" href="#命令条件" aria-label="Permalink to &quot;命令条件&quot;">​</a></h2><p>通过命令添加条件只会在创建条件时将其添加至你手持的物品。因此，你必须向玩家复制并分发这些物品才可使其工作。</p><p>命令：</p><div class="warning custom-block"><p class="custom-block-title">警告</p><p>命令中的 <code>item/armor</code> 表示，设置物品条件输入 <code>item</code>，需要设置装备条件则输入 <code>armor</code>。</p></div><ul><li><code>/sk item/armor requirement add &lt;技能名称&gt; &lt;技能等级&gt; [显示描述]</code> - 对手持物品添加物品/装备条件。<code>技能名称</code> 为指定限制等级的技能名称，<code>技能等级</code> 则为限制的等级。<code>显示描述</code> 为可选填入 <code>true/false</code> 的值，决定了物品是否会显示等级限制的描述。描述与物品要求完全独立，因此你可以对其删改。</li><li><code>/sk item/armor requirement remove &lt;技能名称&gt; [移除描述]</code> - 移除手持物品的物品/装备条件。若 <code>移除描述</code> 为 true，则插件会试图删除原本添加的条件描述。如果你通过其他插件为物品添加了自定义描述，则这个功能可能不会起效。</li><li><code>/sk item/armor requirement list</code> - 列出手持物品包含的物品/装备条件。</li><li><code>/sk item/armor requirement removeall</code> - 移除手持物品包含的所有物品/装备条件。</li></ul><h2 id="全局条件" tabindex="-1">全局条件 <a class="header-anchor" href="#全局条件" aria-label="Permalink to &quot;全局条件&quot;">​</a></h2><p>全局条件可以对某一类物品进行限制。首先你要在 <code>config.yml</code> 下的 <code>requirement.armor</code> 或 <code>requirement.item</code> 下添加一个 <code>global</code> 列表。</p><p>内容条目的格式为 <code>- 材料名称 技能名称:技能等级</code>。</p><p>如下为钻石剑设置近战 10 级条件的全局配置，以及其在配置中的位置：</p><div class="language-YAML vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">YAML</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">requirement</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">:</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">  enabled</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">true</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">  item</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">:</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">    global</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">:</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">    - </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">DIAMOND_SWORD FIGHTING:10</span></span></code></pre></div><p>支持对同一物品添加多个条件：</p><div class="language-YAML vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">YAML</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">requirement</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">:</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">  armor</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">:</span></span>
<span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">    global</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">:</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">    - </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">DIAMOND_CHESTPLATE DEFENSE:10 AGILITY:4</span></span></code></pre></div><div class="info custom-block"><p class="custom-block-title">信息</p><p>全局物品不支持自定义物品。</p></div>`,18)),i(a),i(t)])}const b=l(p,[["render",d]]);export{g as __pageData,b as default};
