import{_ as o,C as t,c as s,o as n,am as i,G as e}from"./chunks/framework.SAy2jMg1.js";const x=JSON.parse('{"title":"图案（方块）","description":"","frontmatter":{"breadcrumbs":[{"title":"Continue Project","link":"/FastAsyncWorldEdit"},{"title":"图案（方块）","link":"/FastAsyncWorldEdit/command-utilties.patterns"}]},"headers":[],"relativePath":"FastAsyncWorldEdit/command-utilties.patterns.md","filePath":"FastAsyncWorldEdit/command-utilties.patterns.md"}'),d={name:"FastAsyncWorldEdit/command-utilties.patterns.md"};function h(c,a,g,p,u,b){const r=t("NolebaseGitContributors"),l=t("NolebaseGitChangelog");return n(),s("div",null,[a[0]||(a[0]=i('<h1 id="图案-方块" tabindex="-1">图案（方块） <a class="header-anchor" href="#图案-方块" aria-label="Permalink to &quot;图案（方块）&quot;">​</a></h1><h2 id="介绍" tabindex="-1">介绍 <a class="header-anchor" href="#介绍" aria-label="Permalink to &quot;介绍&quot;">​</a></h2><p>当前页面正等待改进。</p><p>图案可以应用于多个修改方块的命令中。如 <code>//set &lt;图案&gt;</code> 及 <code>//material &lt;图案&gt;</code></p><h2 id="格式" tabindex="-1">格式 <a class="header-anchor" href="#格式" aria-label="Permalink to &quot;格式&quot;">​</a></h2><h3 id="方块" tabindex="-1">方块 <a class="header-anchor" href="#方块" aria-label="Permalink to &quot;方块&quot;">​</a></h3><p>直接填入方块的名称或 ID（如 <code>stone</code>）。</p><h3 id="界面内物品" tabindex="-1">界面内物品 <a class="header-anchor" href="#界面内物品" aria-label="Permalink to &quot;界面内物品&quot;">​</a></h3><p>你可以使用 <code>hand</code> 或诸如 <code>slot5</code>（<code>slot</code> 后接背包内格子编号）来使用背包内的物品。</p><h3 id="多方块图案" tabindex="-1">多方块图案 <a class="header-anchor" href="#多方块图案" aria-label="Permalink to &quot;多方块图案&quot;">​</a></h3><p>使用英文逗号（<code>,</code>）以随机选取多个方块填充。若要指定占比，你可以使用 <code>%</code>，如 <code>80%stone,20%oak_planks</code> 表示 80% 的石头与 20% 的橡木木板混合组成的图案。</p><h3 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-label="Permalink to &quot;参数&quot;">​</a></h3><p>图案参数应当以方括号标注，如 <code>#simplex[10][stone,oak_log]</code></p><h2 id="图案列表" tabindex="-1">图案列表 <a class="header-anchor" href="#图案列表" aria-label="Permalink to &quot;图案列表&quot;">​</a></h2><h3 id="offset-dx-dy-dz-图案" tabindex="-1">#offset &lt;dx&gt; &lt;dy&gt; &lt;dz&gt; &lt;图案&gt; <a class="header-anchor" href="#offset-dx-dy-dz-图案" aria-label="Permalink to &quot;\\#offset \\&lt;dx\\&gt; \\&lt;dy\\&gt; \\&lt;dz\\&gt; \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 偏移图案</p><h3 id="mask-蒙版-应用图案-排除图案" tabindex="-1">#mask &lt;蒙版&gt; &lt;应用图案&gt; &lt;排除图案&gt; <a class="header-anchor" href="#mask-蒙版-应用图案-排除图案" aria-label="Permalink to &quot;\\#mask \\&lt;蒙版\\&gt; \\&lt;应用图案\\&gt; \\&lt;排除图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 根据蒙版应用图案</p><h3 id="spread-dx-dy-dz-图案" tabindex="-1">#spread &lt;dx&gt; &lt;dy&gt; &lt;dz&gt; &lt;图案&gt; <a class="header-anchor" href="#spread-dx-dy-dz-图案" aria-label="Permalink to &quot;\\#spread \\&lt;dx\\&gt; \\&lt;dy\\&gt; \\&lt;dz\\&gt; \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 随机分散方块</p><h3 id="buffer-图案" tabindex="-1">#buffer &lt;图案&gt; <a class="header-anchor" href="#buffer-图案" aria-label="Permalink to &quot;\\#buffer \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 只在图案使用时放置一次方块<br> 若不需要重复改变同一点的方块，则请将其应用于笔刷</p><h3 id="color-颜色" tabindex="-1">#color &lt;颜色&gt; <a class="header-anchor" href="#color-颜色" aria-label="Permalink to &quot;\\#color \\&lt;颜色\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 使用最贴近指定颜色的方块</p><h3 id="clipboard" tabindex="-1">#clipboard <a class="header-anchor" href="#clipboard" aria-label="Permalink to &quot;\\#clipboard&quot;">​</a></h3><p><strong>描述：</strong> 以剪贴板中的内容为图案</p><h3 id="existing" tabindex="-1">#existing <a class="header-anchor" href="#existing" aria-label="Permalink to &quot;\\#existing&quot;">​</a></h3><p><strong>描述：</strong> 使用现存方块</p><h3 id="biome-生物群系" tabindex="-1">#biome &lt;生物群系&gt; <a class="header-anchor" href="#biome-生物群系" aria-label="Permalink to &quot;\\#biome \\&lt;生物群系\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 设置生物群系</p><h3 id="表达式" tabindex="-1">= &lt;表达式&gt; <a class="header-anchor" href="#表达式" aria-label="Permalink to &quot;= \\&lt;表达式\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 表达式图案：<a href="https://worldedit.enginehub.org/en/latest/usage/other/expressions/#expression-syntax" target="_blank" rel="noreferrer">https://worldedit.enginehub.org/en/latest/usage/other/expressions/#expression-syntax</a> （<a href="/WorldEdit/usage.other.expression-syntax">站内译文链接</a>）</p><h3 id="relative-图案" tabindex="-1">#relative &lt;图案&gt; <a class="header-anchor" href="#relative-图案" aria-label="Permalink to &quot;\\#relative \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 将图案吸附至点击位置</p><h3 id="saturate-r-g-b" tabindex="-1">#saturate &lt;r&gt; &lt;g&gt; &lt;b&gt; <a class="header-anchor" href="#saturate-r-g-b" aria-label="Permalink to &quot;\\#saturate \\&lt;r\\&gt; \\&lt;g\\&gt; \\&lt;b\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 将指定方块染色</p><h3 id="darken" tabindex="-1">#darken <a class="header-anchor" href="#darken" aria-label="Permalink to &quot;\\#darken&quot;">​</a></h3><p><strong>描述：</strong> 将现有方块色调变暗</p><h3 id="anglecolor-距离" tabindex="-1">#anglecolor &lt;距离&gt; <a class="header-anchor" href="#anglecolor-距离" aria-label="Permalink to &quot;\\#anglecolor \\&lt;距离\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 基于现有地形角度设置更暗的方块</p><h3 id="desaturate-百分比" tabindex="-1">#desaturate &lt;百分比&gt; <a class="header-anchor" href="#desaturate-百分比" aria-label="Permalink to &quot;\\#desaturate \\&lt;百分比\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 去除现存方块的颜色</p><h3 id="averagecolor-r-g-b" tabindex="-1">#averagecolor &lt;r&gt; &lt;g&gt; &lt;b&gt; <a class="header-anchor" href="#averagecolor-r-g-b" aria-label="Permalink to &quot;\\#averagecolor \\&lt;r\\&gt; \\&lt;g\\&gt; \\&lt;b\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 从现有方块的颜色与给定颜色取平均值</p><h3 id="fullcopy-结构文件-文件夹-url-copy-rotate-false-flip-false" tabindex="-1">#fullcopy [结构文件|文件夹|url=#copy] [rotate=false] [flip=false] <a class="header-anchor" href="#fullcopy-结构文件-文件夹-url-copy-rotate-false-flip-false" aria-label="Permalink to &quot;\\#fullcopy [结构文件\\|文件夹\\|url=\\#copy] [rotate=false] [flip=false]&quot;">​</a></h3><p><strong>描述：</strong> 在每个方块上放置剪贴板内容。</p><h3 id="buffer2d-图案" tabindex="-1">#buffer2d &lt;图案&gt; <a class="header-anchor" href="#buffer2d-图案" aria-label="Permalink to &quot;\\#buffer2d \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 只在使用图案时改变指定列的方块</p><h3 id="angledata" tabindex="-1">#angledata <a class="header-anchor" href="#angledata" aria-label="Permalink to &quot;\\#angledata&quot;">​</a></h3><p><strong>描述：</strong> 基于现存地形角度改变方块数据</p><h3 id="lighten-随机-true-最大复杂度-100" tabindex="-1">#lighten [随机=true] [最大复杂度=100] <a class="header-anchor" href="#lighten-随机-true-最大复杂度-100" aria-label="Permalink to &quot;\\#lighten [随机=true] [最大复杂度=100]&quot;">​</a></h3><p><strong>描述：</strong> 让现存方块色调变淡</p><h3 id="x-图案" tabindex="-1">#!x &lt;图案&gt; <a class="header-anchor" href="#x-图案" aria-label="Permalink to &quot;\\#!x \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 图案不会提供 Z 轴有关信息。<br> 示例：<code>!x[!z[#~[#l3d[pattern]]]]</code></p><h3 id="surfacespread-距离-图案" tabindex="-1">#surfacespread &lt;距离&gt; &lt;图案&gt; <a class="header-anchor" href="#surfacespread-距离-图案" aria-label="Permalink to &quot;\\#surfacespread \\&lt;距离\\&gt; \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 仅对表面方块生效。选择给定图案匹配方块附近的其他方块（距离不小于 0）。使用 <code>#existing</code> 随机偏移世界内方块，也可使用 <code>#copy</code> 偏移剪贴板内方块</p><h3 id="solidspread-dx-dy-dz-图案" tabindex="-1">#solidspread &lt;dx&gt; &lt;dy&gt; &lt;dz&gt; &lt;图案&gt; <a class="header-anchor" href="#solidspread-dx-dy-dz-图案" aria-label="Permalink to &quot;\\#solidspread \\&lt;dx\\&gt; \\&lt;dy\\&gt; \\&lt;dz\\&gt; \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 随机分散放置固体方块</p><h3 id="linear2d-图案-xscale-1-zscale-1" tabindex="-1">#linear2d &lt;图案&gt; [xscale=1] [zscale=1] <a class="header-anchor" href="#linear2d-图案-xscale-1-zscale-1" aria-label="Permalink to &quot;\\#linear2d \\&lt;图案\\&gt; [xscale=1] [zscale=1]&quot;">​</a></h3><p><strong>描述：</strong> 以 X、Z 轴为坐标系选择图案内的方块</p><h3 id="y-图案" tabindex="-1">#!y &lt;图案&gt; <a class="header-anchor" href="#y-图案" aria-label="Permalink to &quot;\\#!y \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 图案不会提供 Y 轴有关信息。</p><h3 id="linear3d-图案-xscale-1-yscale-1-zscale-1" tabindex="-1">#linear3d &lt;图案&gt; [xscale=1] [yscale=1] [zscale=1] <a class="header-anchor" href="#linear3d-图案-xscale-1-yscale-1-zscale-1" aria-label="Permalink to &quot;\\#linear3d \\&lt;图案\\&gt; [xscale=1] [yscale=1] [zscale=1]&quot;">​</a></h3><p><strong>描述：</strong> 以 X、Y、Z 为坐标系选择图案内的方块</p><h3 id="linear-图案" tabindex="-1">#linear &lt;图案&gt; <a class="header-anchor" href="#linear-图案" aria-label="Permalink to &quot;\\#linear \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 按顺序选择图案内的方块</p><h3 id="z-图案" tabindex="-1">#!z &lt;图案&gt; <a class="header-anchor" href="#z-图案" aria-label="Permalink to &quot;\\#!z \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 图案不会提供 Z 轴有关信息。</p><h3 id="simplex-scale-10-图案" tabindex="-1">#simplex &lt;scale=10&gt; &lt;图案&gt; <a class="header-anchor" href="#simplex-scale-10-图案" aria-label="Permalink to &quot;\\#simplex \\&lt;scale=10\\&gt; \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 通过简单噪声函数生成方块</p><h3 id="perlin-scale-10-图案" tabindex="-1">#perlin &lt;scale=10&gt; &lt;图案&gt; <a class="header-anchor" href="#perlin-scale-10-图案" aria-label="Permalink to &quot;\\#perlin \\&lt;scale=10\\&gt; \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 通过柏林噪声函数生成方块</p><h3 id="rmf-scale-10-图案" tabindex="-1">#rmf &lt;scale=10&gt; &lt;图案&gt; <a class="header-anchor" href="#rmf-scale-10-图案" aria-label="Permalink to &quot;\\#rmf \\&lt;scale=10\\&gt; \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 通过脊状多重分形噪声生成方块</p><h3 id="voronoi-scale-10-图案" tabindex="-1">#voronoi &lt;scale=10&gt; &lt;图案&gt; <a class="header-anchor" href="#voronoi-scale-10-图案" aria-label="Permalink to &quot;\\#voronoi \\&lt;scale=10\\&gt; \\&lt;图案\\&gt;&quot;">​</a></h3><p><strong>描述：</strong> 通过泰森多边形法噪声生成方块</p>',76)),e(r),e(l)])}const m=o(d,[["render",h]]);export{x as __pageData,m as default};
