import{_ as l,C as t,c as s,o as i,am as n,G as a}from"./chunks/framework.SAy2jMg1.js";const m=JSON.parse('{"title":"安装","description":"","frontmatter":{"breadcrumbs":[{"title":"Continue Project","link":"/FastAsyncWorldEdit"},{"title":"安装","link":"/FastAsyncWorldEdit/getting-started.installation"}]},"headers":[],"relativePath":"FastAsyncWorldEdit/getting-started.installation.md","filePath":"FastAsyncWorldEdit/getting-started.installation.md"}'),c={name:"FastAsyncWorldEdit/getting-started.installation.md"};function d(p,e,h,u,g,b){const r=t("NolebaseGitContributors"),o=t("NolebaseGitChangelog");return i(),s("div",null,[e[0]||(e[0]=n('<h1 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h1><h2 id="插件安装" tabindex="-1">插件安装 <a class="header-anchor" href="#插件安装" aria-label="Permalink to &quot;插件安装&quot;">​</a></h2><h3 id="首次安装" tabindex="-1">首次安装 <a class="header-anchor" href="#首次安装" aria-label="Permalink to &quot;首次安装&quot;">​</a></h3><ol><li><a href="https://www.spigotmc.org/resources/13932" target="_blank" rel="noreferrer">下载插件</a>。</li><li>将得到的“FastAsyncWorldEdit.jar”文件放入 <code>plugins</code> 文件夹。</li><li>重启服务器，FAWE 会生成所有必要的文件。</li></ol><div class="info custom-block"><p class="custom-block-title">信息</p><p>6.0.0 或更高版本至少需要 Java 17。</p></div><div class="warning custom-block"><p class="custom-block-title">警告</p><p>请不要安装 WorldEdit！FAWE 可<strong>直接替换</strong>。</p></div><h2 id="第三方插件支持" tabindex="-1">第三方插件支持 <a class="header-anchor" href="#第三方插件支持" aria-label="Permalink to &quot;第三方插件支持&quot;">​</a></h2><h3 id="区域保护插件" tabindex="-1">区域保护插件 <a class="header-anchor" href="#区域保护插件" aria-label="Permalink to &quot;区域保护插件&quot;">​</a></h3><p>如下区域保护插件能够兼容本插件。部分插件可能未在此列出：</p><ul><li><a href="https://www.spigotmc.org/resources/1035" target="_blank" rel="noreferrer">FactionsUUID</a></li><li><a href="https://www.spigotmc.org/resources/68900" target="_blank" rel="noreferrer">GriefDefender</a></li><li><a href="https://www.spigotmc.org/resources/1884" target="_blank" rel="noreferrer">GriefPrevention</a></li><li><a href="https://www.spigotmc.org/resources/77506" target="_blank" rel="noreferrer">PlotSquared</a></li><li><a href="https://www.spigotmc.org/resources/72694" target="_blank" rel="noreferrer">Towny</a></li><li><a href="https://dev.bukkit.org/projects/worldguard" target="_blank" rel="noreferrer">WorldGuard</a></li></ul><div class="info custom-block"><p class="custom-block-title">信息</p><p>可以通过配置或 <code>/wea</code> 命令或 <code>fawe.bypass</code> 权限禁用。</p></div><h3 id="其他插件" tabindex="-1">其他插件 <a class="header-anchor" href="#其他插件" aria-label="Permalink to &quot;其他插件&quot;">​</a></h3><p>我们也提供了一个异步版本的 VoxelSniper，以使其与 FAWE 兼容：<a href="https://dev.bukkit.org/projects/favs" target="_blank" rel="noreferrer">FastAsyncVoxelSniper</a>。</p><h2 id="记录与回滚" tabindex="-1">记录与回滚 <a class="header-anchor" href="#记录与回滚" aria-label="Permalink to &quot;记录与回滚&quot;">​</a></h2><p>在 <code>config.yml</code> 中启用 <code>use-disk</code> 与 <code>use-database</code> 即可使用 FAWE 内置的记录/回滚功能。</p><div class="info custom-block"><p class="custom-block-title">信息</p><p>可以放心地将 FAWE 回滚功能开放给玩家使用。若要绕过日志记录，请使用命令 <code>//fast</code>。</p></div>',16)),a(r),a(o)])}const _=l(c,[["render",d]]);export{m as __pageData,_ as default};
