import{_ as i,C as t,c as r,o as s,an as c,G as o}from"./chunks/framework.CUFjuIqA.js";const b=JSON.parse('{"title":"切换存储类型","description":"","frontmatter":{"breadcrumbs":[{"title":"Continue Project","link":"/LuckPerms"},{"title":"切换存储类型","link":"/LuckPerms/how-to.switch-storage-types"}]},"headers":[],"relativePath":"LuckPerms/how-to.switch-storage-types.md","filePath":"LuckPerms/how-to.switch-storage-types.md"}'),n={name:"LuckPerms/how-to.switch-storage-types.md"};function d(p,e,h,u,m,_){const a=t("NolebaseGitContributors"),l=t("NolebaseGitChangelog");return s(),r("div",null,[e[0]||(e[0]=c('<h1 id="切换存储类型" tabindex="-1">切换存储类型 <a class="header-anchor" href="#切换存储类型" aria-label="Permalink to &quot;切换存储类型&quot;">​</a></h1><h2 id="操作步骤" tabindex="-1">操作步骤 <a class="header-anchor" href="#操作步骤" aria-label="Permalink to &quot;操作步骤&quot;">​</a></h2><p>切换存储类型非常简单。</p><ol><li>首先，输入命令 <code>/luckperms export &lt;文件&gt;</code>。</li><li>然后，完全关闭服务器。</li><li>编辑配置文件并修改存储类型。</li><li>重启服务器并让数据库初始化。</li><li>之后，输入命令 <code>/luckperms import &lt;文件&gt;</code>。</li><li>所有数据都进入了新数据库。</li></ol><p>&lt;文件&gt; 是你想要保存/读取数据的文件名称。文件需要处于 LuckPerms 的 data 文件夹下。在导出命令中的文件名称不作限制（如 <code>data</code>）—— 但在再次导入时你应该使用与保存时相同的文件名称。</p><h2 id="网页导入" tabindex="-1">网页导入 <a class="header-anchor" href="#网页导入" aria-label="Permalink to &quot;网页导入&quot;">​</a></h2><ol><li>输入命令 <code>/luckperms export --upload</code>。</li><li>保存出现的 10 位验证码备用。</li><li>完全关闭服务器。</li><li>编辑配置文件并修改存储类型。</li><li>输入命令 <code>/luckperms import &lt;验证码&gt; --upload</code></li><li>所有数据都进入了新数据库。</li></ol><h2 id="备份" tabindex="-1">备份 <a class="header-anchor" href="#备份" aria-label="Permalink to &quot;备份&quot;">​</a></h2><p>你可以使用本功能来备份所有的 LuckPerms 数据。只需输入导出命令并将文件保存在安全的地方即可。需要注意的是 <code>--upload</code> 参数导出的保存内容会超时，并不是长久保存的理想方法。</p>',9)),o(a),o(l)])}const P=i(n,[["render",d]]);export{b as __pageData,P as default};
