import{_ as i,C as a,c as s,o as n,an as d,G as t}from"./chunks/framework.CUFjuIqA.js";const v=JSON.parse('{"title":"群系菜单","description":"","frontmatter":{"breadcrumbs":[{"title":"Continue Project","link":"/SuperiorSkyblock2"},{"title":"群系菜单","link":"/SuperiorSkyblock2/overview.menus.biomes-menu"}]},"headers":[],"relativePath":"SuperiorSkyblock2/overview.menus.biomes-menu.md","filePath":"SuperiorSkyblock2/overview.menus.biomes-menu.md"}'),c={name:"SuperiorSkyblock2/overview.menus.biomes-menu.md"};function l(h,e,p,u,b,m){const o=a("NolebaseGitContributors"),r=a("NolebaseGitChangelog");return n(),s("div",null,[e[0]||(e[0]=d('<h1 id="群系菜单" tabindex="-1">群系菜单 <a class="header-anchor" href="#群系菜单" aria-label="Permalink to &quot;群系菜单&quot;">​</a></h1><p>在这里你可以找到自定义生物群系的教程。</p><h2 id="全局设置" tabindex="-1">全局设置 <a class="header-anchor" href="#全局设置" aria-label="Permalink to &quot;全局设置&quot;">​</a></h2><p>首先，通过 <code>title</code>（字符串）设置为菜单赋予自定义标题。之后，你可以自行配置其他选项：<code>previous-menu</code>（布尔值）决定是否在关闭菜单后显示上级菜单，<code>type</code>（字符串）决定菜单显示的界面类型，<code>open-sound</code>（声音格式）可以在打开菜单时播放自定义音效。</p><div class="info custom-block"><p class="custom-block-title">信息</p><p>你可以在<a href="https://hub.spigotmc.org/javadocs/bukkit/org/bukkit/event/inventory/InventoryType.html" target="_blank" rel="noreferrer">这里</a>找到有效的界面类型。</p></div><h2 id="排版设置" tabindex="-1">排版设置 <a class="header-anchor" href="#排版设置" aria-label="Permalink to &quot;排版设置&quot;">​</a></h2><p>让我们着手研究排版。排版部分决定了菜单的显示样式。这是一系列字符串，其中的每个字符都代表着菜单中显示的一个按钮。它应当包含一至六行的字符串，每个字符串包含九个字符——空格不算作字符。如果不同格子有两个相同的字符，则这两个格子会显示相同的物品。物品的实际显示内容不在这里配置，而是在 <code>items</code> 部分。</p><h2 id="群系相关设置" tabindex="-1">群系相关设置 <a class="header-anchor" href="#群系相关设置" aria-label="Permalink to &quot;群系相关设置&quot;">​</a></h2><p>群系菜单有一些可配置的额外设置——<code>current-biome-glow</code>，可以决定当前选中的生物群系是否在菜单中带有附魔光效。</p><h2 id="物品设置" tabindex="-1">物品设置 <a class="header-anchor" href="#物品设置" aria-label="Permalink to &quot;物品设置&quot;">​</a></h2><p>这就是你配置物品的地方。你可以在<a href="./overview.menus#编辑菜单内的物品">这里</a>找到如何正确配置物品的教程。</p><h3 id="群系物品" tabindex="-1">群系物品 <a class="header-anchor" href="#群系物品" aria-label="Permalink to &quot;群系物品&quot;">​</a></h3><p>群系菜单使得玩家能够自由切换岛屿的生物群系。为了实现这个功能，菜单必须添加带有特殊功能的自定义按钮。</p><p>这些特殊类型的按钮与正常类型的物品格式不同，且有一些必须设置的内容。</p><table tabindex="0"><thead><tr><th>名称</th><th>类型</th><th>描述</th></tr></thead><tbody><tr><td><code>biome</code></td><td>字符串</td><td>待更换的生物群系。可在<a href="https://hub.spigotmc.org/javadocs/spigot/org/bukkit/block/Biome.html" target="_blank" rel="noreferrer">这里</a>找到完整的生物群系列表。</td></tr><tr><td><code>access</code></td><td>物品配置</td><td>玩家有权限更换生物群系时显示的物品样式。</td></tr><tr><td><code>no-access</code></td><td>物品配置</td><td>玩家无权限更换生物群系时显示的物品样式。</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">信息</p><p><code>access</code> 与 <code>no-access</code> 部分可使用声音与命令配置来实现玩家在拥有/缺少权限的情况下触发自定义的声音/命令。</p></div><h2 id="声音设置" tabindex="-1">声音设置 <a class="header-anchor" href="#声音设置" aria-label="Permalink to &quot;声音设置&quot;">​</a></h2><p>在这部分配置中你可以自定义玩家点击物品时发出的音效。你可以在<a href="./overview.menus#物品音效">这里</a>找到正确配置音效的教程。</p><h2 id="权限设置" tabindex="-1">权限设置 <a class="header-anchor" href="#权限设置" aria-label="Permalink to &quot;权限设置&quot;">​</a></h2><p>在这部分配置中你可以自定义玩家点击物品所需要的权限。你可以在<a href="./overview.menus#权限部分">这里</a>找到正确配置权限条件的教程。</p><h2 id="菜单实例" tabindex="-1">菜单实例 <a class="header-anchor" href="#菜单实例" aria-label="Permalink to &quot;菜单实例&quot;">​</a></h2><p>这是生物群系菜单的示例配置，涵盖了本章节教程提及的大部分技术及功能。</p><div class="language-YAML vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">YAML</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># 原文尚无内容显示...</span></span></code></pre></div>',23)),t(o),t(r)])}const _=i(c,[["render",l]]);export{v as __pageData,_ as default};
